    }
}
```

## Server.ts

```ts
import express from "express";
import next from "next";
import { parse } from "url";
import { join } from "path";

const app = getApp();
const handle = app.getRequestHandler();
prepareApp();

function getApp() {
    const dev = process.env.NODE_ENV !== "production";
    const dir = __dirname; // todo - where the next project is located (needs to be)
    const quiet = false;
    return next({ dev, dir, quiet });
}

async function prepareApp() {
    try {
        await app.prepare();
        const server = express();

        server.get("/p/:id", (req, res) => {
            const actualPage = "/post";
            const queryParams = { title: req.params.id };
            app.render(req, res, actualPage, queryParams);
        });

        server.get("*", (req, res) => {
            const parsedUrl = parse(req.url, true);
            const rootStaticFiles = ["/robots.txt", "/sitemap.xml", "/favicon.ico"];
            const pathname = parsedUrl.pathname;
            if (pathname && rootStaticFiles.indexOf(pathname) > -1) {
                const path = join(__dirname, "static", pathname);
                app.serveStatic(req, res, path);
            } else {
                handle(req, res, parsedUrl);
            }
        });

        const port = process.env.PORT;
        server.listen(port, (err: Error) => {
            if (err) throw err;
            console.log(`> Ready on http://localhost:${port}`);
        });
    } catch (ex) {
        console.error(ex.stack);
        process.exit(1);
    }
}
```

## docker-compose.yaml

```yaml
version: "3"
services:
    web:
        build: ./
        volumes:
            # force container to use host machine's build folder instead of own build folder
            - ./build:/usr/src/app/build
        command: npm run debug
        ports:
            - "3001:3001"
            - "9223:9223"
        environment:
            - NODE_ENV=development
            - PORT=3001
```

## Dockerfile

```Dockerfile
FROM node:10
WORKDIR /usr/src/app
RUN npm install -g typescript nodemon

# extra copy creates separate image layer, preventing unnecessary npm installs
COPY package*.json ./
RUN npm install

COPY ./build ./build
EXPOSE $PORT
CMD [ "npm", "start" ]
```

